import React, { Component } from 'react';
import {connect} from 'react-redux'


class WizardForm extends Component {

  state = {
    name: "",
    house: "",
    redux: ""
  }

  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  handleSubmit = (e) => {
    e.preventDefault()
    console.log("Submitted!");
  }

  render() {
    let {name, house, redux} = this.state
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          Name
        </label>
          <input type="text" name="name" onChange={this.handleChange} value={name}/>
        <label>
          House
        </label>
          <input type="text" name="house" onChange={this.handleChange} value={house}/>
        <label>
          Redux
        </label>
          <input type="text" name="redux" onChange={this.handleChange} value={redux}/>
        <input type="submit"/>
      </form>
    );
  }

}




// actionCreator => addWizard (func Definition)
// actionObject => action (POJO)
const addWizard = (wizzyObject) => {
  let actionObject = {
    type: "ADD_WIZARD",
    payload: wizzyObject
  }

  return actionObject

}











































export default connect()(WizardForm);
